CREATE TABLE SPROKET (IDSPROKET INTEGER AUTO_INCREMENT NOT NULL, NAME VARCHAR(255), PRIMARY KEY (IDSPROKET))
CREATE TABLE CRANKSET (IDCRANKSET INTEGER AUTO_INCREMENT NOT NULL, INNERGEARTOOTH INTEGER, MIDDLEGEARTOOTH INTEGER, NAME VARCHAR(255), OUTERGEARTOOTH INTEGER, PRIMARY KEY (IDCRANKSET))
CREATE TABLE PERIMETER (IDPERIMETER INTEGER AUTO_INCREMENT NOT NULL, ETRTO VARCHAR(255), FRENCH VARCHAR(255), PRIMARY KEY (IDPERIMETER))
CREATE TABLE SPROKETDETAIL (TOOTH INTEGER, IDSPROKET INTEGER NOT NULL, TOOTHINDEX INTEGER NOT NULL, PRIMARY KEY (IDSPROKET, TOOTHINDEX))
ALTER TABLE SPROKETDETAIL ADD CONSTRAINT FK_SPROKETDETAIL_IDSPROKET FOREIGN KEY (IDSPROKET) REFERENCES SPROKET (IDSPROKET)
